import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as h,o as s,c as o,a,b as e,d,e as r}from"./app-61a3cc4b.js";const i="/assets/4-3a6ff362.png",c={},l=r('<h1 id="도서-리뷰-시리즈-프레임워크-없는-프론트엔드-개발" tabindex="-1"><a class="header-anchor" href="#도서-리뷰-시리즈-프레임워크-없는-프론트엔드-개발" aria-hidden="true">#</a> 도서 리뷰 시리즈 - 프레임워크 없는 프론트엔드 개발</h1><h2 id="출처" tabindex="-1"><a class="header-anchor" href="#출처" aria-hidden="true">#</a> 출처</h2><p>프란세스코 스트라츨로. 『프레임워크 없는 프론트엔드 개발』. 류영선(역). 에이콘출판사, 2021.</p><h2 id="한-줄-리뷰" tabindex="-1"><a class="header-anchor" href="#한-줄-리뷰" aria-hidden="true">#</a> 한 줄 리뷰</h2><p>프레임워크를 직접 만드는 방법을 따라하면서 우리가 사용 중인 프레임워크를 이해하기 좋습니다.</p><h2 id="프레임워크-없는-운동" tabindex="-1"><a class="header-anchor" href="#프레임워크-없는-운동" aria-hidden="true">#</a> 프레임워크 없는 운동</h2>',6),p=a("strong",null,"프레임워크 없이 효과적으로 작업하는 방법",-1),_=a("strong",null,"프로젝트에 적합한 프레임워크를 선택하는 방법",-1),f={href:"https://www.frameworklessmovement.org/",target:"_blank",rel:"noopener noreferrer"},u=r('<p>주요 관심사 중 하나는 사람들이 <strong>프레임워크 없이 작업하는 것이 진정으로 가능하다는 것을 이해하게 돕는 것</strong>과 <strong>기술적 의사 결정의 중요성을 이해하게 돕는 노력</strong>을 하는 것이다.</p><p>프레임워크 없는 옵션만 옳다고 주장하지는 않는다. 때에 따라서는 적절한 프레임워크의 선택이 개발 시간의 단축과 프로젝트의 성공에 큰 도움이 된다는 사실을 인정하고 체계적인 분석과 의사 결정 기법에 따라 &#39;적합한&#39; 프레임워크를 선택하는 방법도 설명한다.</p><h2 id="프레임워크에-대한-이야기" tabindex="-1"><a class="header-anchor" href="#프레임워크에-대한-이야기" aria-hidden="true">#</a> 프레임워크에 대한 이야기</h2><h3 id="프레임워크란" tabindex="-1"><a class="header-anchor" href="#프레임워크란" aria-hidden="true">#</a> 프레임워크란</h3><p>코드, 프레임워크, 라이브러리 간의 관계</p><p><img src="'+i+'" alt="프레임워크란"></p><h3 id="기술-부채" tabindex="-1"><a class="header-anchor" href="#기술-부채" aria-hidden="true">#</a> 기술 부채</h3><p>워드 커닝햄은 기술 부채라는 개념을 도입했다. 기술 부채는 기존 기능의 변경이나 새로운 기능의 추가에 따르는 비용은 시간이 지남에 따라 기하급수적으로 늘어나는 것을 의미한다.</p><h3 id="프레임워크-비용과-기술-투자" tabindex="-1"><a class="header-anchor" href="#프레임워크-비용과-기술-투자" aria-hidden="true">#</a> 프레임워크 비용과 기술 투자</h3><p>프레임워크는 아키텍처 자체에 이미 비용을 포함하고 있다. 하지만 합당한 이유로 선정된 프레임워크는 비용이 아니라 자산이다.</p>',10);function m(x,b){const n=h("ExternalLinkIcon");return s(),o("div",null,[l,a("p",null,[e("이 도서는 "),p,e("과 "),_,e("의 두 가지 주제를 다룬다. 이 운동의 선언문은 "),a("a",f,[e("공식 웹사이트"),d(n)]),e("에 게시돼 있다.")]),u])}const v=t(c,[["render",m],["__file","4.html.vue"]]);export{v as default};
