    <span class="token keyword">const</span> events <span class="token operator">=</span> <span class="token punctuation">{</span>
      <span class="token function-variable function">upCount</span><span class="token operator">:</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token function">setCount</span><span class="token punctuation">(</span>count <span class="token operator">+</span> <span class="token number">1</span><span class="token punctuation">)</span>
    <span class="token punctuation">}</span>
   
